import React, {useContext} from "react"
import {ThemeContext} from "./themeContext"

function About(){
    const {theme, toggleTheme} = useContext(ThemeContext)
    return (
        <main className={`${theme}-theme-body`}>
            <h1>This is a website fully devoted to switching between two color themes.</h1>
            <h2>Press the button to see how it works!</h2>
            <button className={`${theme}-theme-body`} onClick={toggleTheme}>Switch Theme</button>
        </main>
    )
}

export default About
